	//第一段参数
	fmt.Println(hex.EncodeToString(encrypt1))
	encrypt2, err := rsAcomponent.Encrypt([]byte(a2))
	if err != nil {
		fmt.Println(err.Error())
		return
	}
	//第二段参数
	fmt.Println(hex.EncodeToString(encrypt2))
	encrypt3, err := rsAcomponent.Encrypt([]byte(a3))
